function defaultData(){return{partA:0,partB:0,operator:"",answer:0,correct:0,time:"00:00:00",timeStart:0,errors:0,totalProblems:20,userAnswer:""}}var app=new Vue({el:"#app",data:function(){return defaultData()},methods:{startTimer:function(){this.timeStart=new Date,started=setInterval(this.clockRunning,10)},stopTimer:function(){clearInterval(this.timeStart),this.time="00:00:00"},clockRunning:function(){var e=new Date,t=new Date(e-this.timeStart),r=(t.getUTCHours(),t.getUTCMinutes()),s=t.getUTCSeconds(),a=t.getUTCMilliseconds();this.time=r+":"+s+":"+a},checkAnswer:function(){Number(this.userAnswer)==this.answer?(this.correct+=1,this.correct==this.totalProblems?(alert("Winner"),this.resetBoard()):this.generateProblem()):this.errors+=1},generateProblem:function(){switch(val=this.randomNumber(1,4),a=0,b=0,val){case 1:this.operator="-",a=this.randomNumber(10,100),b=this.randomNumber(1,a),this.answer=a-b;break;case 2:this.operator="x",a=this.randomNumber(2,15),b=this.randomNumber(2,15),this.answer=a*b;break;case 3:this.operator="&divide;",a=this.randomNumber(2,15),b=this.randomNumber(2,10),c=a*b,this.answer=a,a=c;break;default:this.operator="+",a=this.randomNumber(1,50),b=this.randomNumber(1,50),this.answer=a+b}this.partA=a,this.partB=b,this.userAnswer=""},resetBoard:function(){def=defaultData(),Object.assign(this.$data,def),this.startTimer(),this.generateProblem()},randomNumber:function(e,t){return Math.floor(Math.random()*t)+e}},mounted:function(){(self=this).resetBoard(),window.addEventListener("keypress",function(e){pressed=String.fromCharCode(e.keyCode),"R"==pressed||"r"==pressed?self.resetBoard():"a"==pressed?self.userAnswer=self.answer:"13"==e.keyCode&&self.checkAnswer()})}});